import React, { useState, useRef } from 'react';
import { Upload, FileText, Sparkles, Brain, Target, Zap } from 'lucide-react';

export default function App() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [dragActive, setDragActive] = useState(false);
  const inputRef = useRef(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDragActive(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      setFile(e.dataTransfer.files[0]);
    }
  };

  const handleDrag = (e) => {
    e.preventDefault();
    if (e.type === "dragenter" || e.type === "dragover") setDragActive(true);
    else if (e.type === "dragleave") setDragActive(false);
  };

  const handleUpload = async () => {
    if (!file) return alert('Please select a CSV file first');
    const formData = new FormData();
    formData.append('file', file);
    setLoading(true);
    try {
      // Simulate API call - replace with actual axios call
      // const res = await axios.post('http://localhost:5000/predict', formData);
      // setData(res.data);

      // Mock data for demo
      setTimeout(() => {
        setData([
          {
            ABSTRACT: "This research explores the application of machine learning algorithms in predicting stock market trends using historical data and sentiment analysis.",
            Predicted_Tags: ["Machine Learning", "Finance", "Data Analysis", "Predictive Modeling"]
          },
          {
            ABSTRACT: "A comprehensive study on the effects of climate change on marine ecosystems and biodiversity conservation strategies.",
            Predicted_Tags: ["Climate Change", "Marine Biology", "Conservation", "Environmental Science"]
          }
        ]);
        setLoading(false);
      }, 2000);
    } catch (err) {
      alert('Upload failed or invalid file format.');
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 relative overflow-hidden">
      {/* Animated background elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute -top-40 -right-40 w-80 h-80 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse"></div>
        <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-blue-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse delay-700"></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-80 h-80 bg-pink-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse delay-1000"></div>
      </div>

      {/* Floating particles */}
      <div className="absolute inset-0 pointer-events-none">
        {[...Array(20)].map((_, i) => (
          <div
            key={i}
            className="absolute w-2 h-2 bg-white rounded-full opacity-10"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animation: `float ${3 + Math.random() * 4}s ease-in-out infinite`,
              animationDelay: `${Math.random() * 2}s`
            }}
          />
        ))}
      </div>

      <div className="relative z-10 min-h-screen flex items-center justify-center p-6">
        <div className="w-full max-w-6xl">
          {/* Header */}
          <div className="text-center mb-12">
            <div className="flex justify-center mb-6">
              <div className="relative">
                <div className="absolute inset-0 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full blur-2xl opacity-30 animate-pulse"></div>
                <div className="relative bg-gradient-to-br from-blue-500 to-purple-600 p-4 rounded-full">
                  <Brain className="w-12 h-12 text-white" />
                </div>
              </div>
            </div>
            <h1 className="text-6xl font-black bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400 bg-clip-text text-transparent mb-4 tracking-tight">
              AI Tag Predictor
            </h1>
            <p className="text-xl text-gray-300 max-w-2xl mx-auto leading-relaxed">
              Harness the power of artificial intelligence to automatically generate intelligent tags for your research articles
            </p>
          </div>

          {/* Main Card */}
          <div className="bg-white/5 backdrop-blur-xl rounded-3xl border border-white/10 shadow-2xl p-8 mb-8">
            {/* Upload Section */}
            <div className="mb-8">
              <div className="flex items-center gap-3 mb-6">
                <Upload className="w-6 h-6 text-blue-400" />
                <h2 className="text-2xl font-bold text-white">Upload Your Data</h2>
              </div>

              <div
                className={`relative group transition-all duration-300 border-2 border-dashed rounded-2xl p-12 flex flex-col items-center justify-center cursor-pointer overflow-hidden ${
                  dragActive 
                    ? 'border-blue-400 bg-blue-500/10 scale-105' 
                    : 'border-gray-600 bg-gray-800/30 hover:border-blue-500 hover:bg-blue-500/5'
                }`}
                onClick={() => inputRef.current.click()}
                onDragEnter={handleDrag}
                onDragLeave={handleDrag}
                onDragOver={handleDrag}
                onDrop={handleDrop}
              >
                {/* Animated background */}
                <div className="absolute inset-0 bg-gradient-to-r from-blue-500/0 via-purple-500/5 to-pink-500/0 opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>

                <input
                  ref={inputRef}
                  type="file"
                  accept=".csv"
                  onChange={handleFileChange}
                  className="hidden"
                />

                <div className="relative z-10 flex flex-col items-center">
                  <div className="mb-6 relative">
                    <div className="absolute inset-0 bg-blue-500 rounded-full blur-xl opacity-20 group-hover:opacity-40 transition-opacity duration-300"></div>
                    <div className="relative bg-gradient-to-br from-blue-500 to-purple-600 p-6 rounded-full group-hover:scale-110 transition-transform duration-300">
                      <FileText className="w-12 h-12 text-white" />
                    </div>
                  </div>

                  <div className="text-center">
                    <p className="text-2xl font-bold text-white mb-2">
                      Drop your CSV file here
                    </p>
                    <p className="text-gray-400 text-lg">
                      or <span className="text-blue-400 hover:text-blue-300 transition-colors">click to browse</span>
                    </p>
                  </div>

                  {file && (
                    <div className="mt-6 bg-gray-800/50 backdrop-blur-sm rounded-xl p-4 border border-gray-700">
                      <div className="flex items-center gap-3">
                        <FileText className="w-5 h-5 text-blue-400" />
                        <div>
                          <p className="font-semibold text-white">{file.name}</p>
                          <p className="text-sm text-gray-400">{(file.size / 1024).toFixed(1)} KB</p>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>

            {/* Action Button */}
            <div className="flex justify-center">
              <button
                onClick={handleUpload}
                disabled={loading || !file}
                className="group relative overflow-hidden bg-gradient-to-r from-blue-600 via-purple-600 to-pink-600 hover:from-blue-700 hover:via-purple-700 hover:to-pink-700 text-white font-bold py-4 px-8 rounded-2xl shadow-2xl transition-all duration-300 transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed disabled:hover:scale-100"
              >
                <div className="absolute inset-0 bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400 opacity-0 group-hover:opacity-20 transition-opacity duration-300"></div>
                <div className="relative flex items-center gap-3 text-lg">
                  {loading ? (
                    <>
                      <div className="w-6 h-6 border-2 border-white/30 border-t-white rounded-full animate-spin"></div>
                      <span>Processing Magic...</span>
                    </>
                  ) : (
                    <>
                      <Sparkles className="w-6 h-6" />
                      <span>Generate Tags</span>
                    </>
                  )}
                </div>
              </button>
            </div>
          </div>

          {/* Results Section */}
          {data.length > 0 && (
            <div className="bg-white/5 backdrop-blur-xl rounded-3xl border border-white/10 shadow-2xl p-8 animate-fade-in">
              <div className="flex items-center gap-3 mb-8">
                <Target className="w-6 h-6 text-green-400" />
                <h2 className="text-2xl font-bold text-white">Prediction Results</h2>
                <div className="flex items-center gap-2 ml-auto">
                  <Zap className="w-4 h-4 text-yellow-400" />
                  <span className="text-sm text-gray-400">{data.length} articles processed</span>
                </div>
              </div>

              <div className="space-y-6">
                {data.map((row, idx) => (
                  <div 
                    key={idx} 
                    className="bg-gray-800/40 backdrop-blur-sm rounded-2xl p-6 border border-gray-700/50 hover:border-gray-600 transition-all duration-300 hover:shadow-xl"
                    style={{ animationDelay: `${idx * 0.1}s` }}
                  >
                    <div className="grid lg:grid-cols-3 gap-6">
                      <div className="lg:col-span-2">
                        <h3 className="text-sm font-semibold text-blue-400 mb-3 uppercase tracking-wide">Abstract</h3>
                        <p className="text-gray-300 leading-relaxed text-justify">{row.ABSTRACT}</p>
                      </div>
                      <div>
                        <h3 className="text-sm font-semibold text-purple-400 mb-3 uppercase tracking-wide">Predicted Tags</h3>
                        <div className="flex flex-wrap gap-2">
                          {row.Predicted_Tags.map((tag, i) => (
                            <span
                              key={i}
                              className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-gradient-to-r from-blue-500/20 to-purple-500/20 text-blue-300 border border-blue-500/30 hover:from-blue-500/30 hover:to-purple-500/30 transition-all duration-200 cursor-default"
                            >
                              {tag}
                            </span>
                          ))}
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>

      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0px) rotate(0deg); }
          50% { transform: translateY(-20px) rotate(10deg); }
        }

        @keyframes fade-in {
          from { opacity: 0; transform: translateY(20px); }
          to { opacity: 1; transform: translateY(0); }
        }

        .animate-fade-in {
          animation: fade-in 0.6s ease-out forwards;
        }
      `}</style>
    </div>
  );
}
