import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) {
      alert("Please select a CSV file.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    setLoading(true);
    try {
      const response = await axios.post("http://localhost:5000/predict", formData);
      setData(response.data);
    } catch (error) {
      console.error(error);
      alert("Upload failed. Make sure your Flask backend is running.");
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center px-4 py-12">
      <div className="w-full max-w-6xl bg-white shadow-lg rounded-2xl p-8">
        <h1 className="text-3xl font-bold text-center text-gray-800 mb-8">
          Article Tag Predictor
        </h1>

        <div className="flex flex-col md:flex-row items-center gap-4 justify-center mb-6">
          <input
            type="file"
            accept=".csv"
            onChange={handleFileChange}
            className="w-full md:w-auto border border-gray-300 p-2 rounded"
          />
          <button
            onClick={handleUpload}
            className="bg-blue-600 hover:bg-blue-700 text-white px-5 py-2 rounded transition-all"
          >
            Predict Tags
          </button>
        </div>

        {loading && (
          <p className="text-center text-gray-600 text-lg">Processing file...</p>
        )}

        {data.length > 0 && (
          <div className="mt-8 overflow-x-auto">
            <table className="min-w-full table-auto border border-gray-300 text-sm">
              <thead className="bg-gray-200 text-gray-800">
                <tr>
                  <th className="p-3 text-left border">Abstract</th>
                  <th className="p-3 text-left border">Predicted Tags</th>
                </tr>
              </thead>
              <tbody>
                {data.map((row, idx) => (
                  <tr key={idx} className={idx % 2 === 0 ? 'bg-white' : 'bg-gray-50'}>
                    <td className="p-3 border align-top text-justify">{row.ABSTRACT}</td>
                    <td className="p-3 border align-top">{row.Predicted_Tags.join(', ')}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
