import React, { useState } from 'react';
import axios from 'axios';

export default function App() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) return alert('Please select a CSV file first');

    const formData = new FormData();
    formData.append('file', file);

    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/predict', formData);
      setData(res.data);
    } catch (err) {
      console.error(err);
      alert('Upload failed or invalid file format.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center px-4 py-10">
      <div className="bg-white shadow-lg rounded-xl p-10 w-full max-w-6xl border border-gray-200">
        <h1 className="text-3xl font-bold text-center text-gray-800 mb-8">
          Article Tag Predictor
        </h1>

        <div className="flex flex-col md:flex-row items-center gap-4 mb-6 justify-center">
          <input
            type="file"
            accept=".csv"
            onChange={handleFileChange}
            className="border border-gray-300 p-3 rounded-md w-full md:w-auto"
          />
          <button
            onClick={handleUpload}
            className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-md transition-all duration-300"
          >
            Predict Tags
          </button>
        </div>

        {loading && <p className="text-center text-lg text-gray-600">Processing...</p>}

        {data.length > 0 && (
          <div className="overflow-x-auto mt-8">
            <table className="min-w-full border-collapse bg-white rounded-md overflow-hidden">
              <thead className="bg-gray-100 text-gray-700">
                <tr>
                  <th className="p-4 text-left text-sm font-medium">Abstract</th>
                  <th className="p-4 text-left text-sm font-medium">Predicted Tags</th>
                </tr>
              </thead>
              <tbody>
                {data.map((row, idx) => (
                  <tr key={idx} className={idx % 2 === 0 ? "bg-white" : "bg-gray-50"}>
                    <td className="p-4 text-gray-800 text-sm align-top w-2/3 text-justify">{row.ABSTRACT}</td>
                    <td className="p-4 text-gray-600 text-sm align-top w-1/3">{row.Predicted_Tags.join(', ')}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}
