import React, { useState } from 'react';
import axios from 'axios';

export default function App() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) return alert('Please select a CSV file first');

    const formData = new FormData();
    formData.append('file', file);

    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/predict', formData);
      setData(res.data);
    } catch (err) {
      console.error(err);
      alert('Upload failed or invalid file format.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8 flex justify-center items-start">
      <div className="bg-white shadow-xl rounded-2xl p-8 w-full max-w-5xl">
        <h1 className="text-3xl font-bold text-gray-800 mb-6 flex items-center">
          <span className="mr-2 text-pink-600 text-4xl">üß†</span> Article Tag Predictor
        </h1>

        <div className="flex items-center gap-4 mb-6">
          <input
            type="file"
            accept=".csv"
            onChange={handleFileChange}
            className="border border-gray-300 p-2 rounded w-full"
          />
          <button
            onClick={handleUpload}
            className="bg-blue-600 hover:bg-blue-700 text-white px-5 py-2 rounded shadow"
          >
            Predict Tags
          </button>
        </div>

        {loading && <p className="text-gray-600 mt-2">‚è≥ Processing...</p>}

        {data.length > 0 && (
          <div className="overflow-x-auto mt-6">
            <table className="min-w-full bg-white border border-gray-300 rounded-lg shadow-sm">
              <thead>
                <tr className="bg-gray-200 text-gray-700 text-left">
                  <th className="p-4 border-b">üìÑ Abstract</th>
                  <th className="p-4 border-b">üè∑Ô∏è Predicted Tags</th>
                </tr>
              </thead>
              <tbody>
                {data.map((row, idx) => (
                  <tr key={idx} className="even:bg-gray-50">
                    <td className="p-4 border-b align-top text-sm text-gray-800">{row.ABSTRACT}</td>
                    <td className="p-4 border-b align-top text-sm text-blue-800 font-medium">
                      {row.Predicted_Tags.join(', ')}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}
