import React, { useState } from 'react';
import axios from 'axios';

export default function App() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) return alert("Please select a CSV file first");

    const formData = new FormData();
    formData.append('file', file);

    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/predict', formData);
      setData(res.data);
    } catch (err) {
      console.error(err);
      alert("Upload failed or invalid file format.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-2xl font-bold mb-4">ðŸ§  Article Tag Predictor</h1>

      <input type="file" accept=".csv" onChange={handleFileChange} className="mb-2" />
      <button onClick={handleUpload} className="bg-blue-600 text-white px-4 py-2 rounded">
        Predict Tags
      </button>

      {loading && <p className="mt-4">Processing...</p>}

      {data.length > 0 && (
        <table className="mt-6 w-full border">
          <thead>
            <tr>
              <th className="border p-2 text-left">Abstract</th>
              <th className="border p-2 text-left">Predicted Tags</th>
            </tr>
          </thead>
          <tbody>
            {data.map((row, idx) => (
              <tr key={idx}>
                <td className="border p-2">{row.ABSTRACT}</td>
                <td className="border p-2">{row.Predicted_Tags.join(", ")}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
