import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

export default function App() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) return alert("Please select a CSV file first");

    const formData = new FormData();
    formData.append('file', file);

    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/predict', formData);
      setData(res.data);
    } catch (err) {
      console.error(err);
      alert("Upload failed or invalid file format.");
    } finally {
      setLoading(false);
    }
  };

  const handleDownload = () => {
    const csvContent = "data:text/csv;charset=utf-8,"
      + ["ABSTRACT,Predicted_Tags"]
      .concat(data.map(row => `"${row.ABSTRACT.replace(/"/g, '""')}","${row.Predicted_Tags.join(';')}"`))
      .join("\n");

    const encodedUri = encodeURI(csvContent);
    const link = document.createElement("a");
    link.setAttribute("href", encodedUri);
    link.setAttribute("download", "predictions.csv");
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
        <img src="/logo192.png" alt="Logo" style={{ height: '50px', marginRight: '15px' }} />
        <h1 className="text-2xl font-bold">Article Tag Predictor</h1>
      </div>

      <input type="file" accept=".csv" onChange={handleFileChange} className="mb-2" />
      <button onClick={handleUpload} className="bg-blue-600 text-white px-4 py-2 rounded mr-4">
        Predict Tags
      </button>
      {data.length > 0 && (
        <button onClick={handleDownload} className="bg-green-600 text-white px-4 py-2 rounded">
          Download CSV
        </button>
      )}

      {loading && <p className="mt-4">Processing...</p>}

      {data.length > 0 && (
        <table className="mt-6 w-full border">
          <thead>
            <tr>
              <th className="border p-2 text-left">Abstract</th>
              <th className="border p-2 text-left">Predicted Tags</th>
            </tr>
          </thead>
          <tbody>
            {data.map((row, idx) => (
              <tr key={idx}>
                <td className="border p-2">{row.ABSTRACT}</td>
                <td className="border p-2">{row.Predicted_Tags.join(", ")}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
