import React, { useState } from 'react';
import axios from 'axios';

export default function App() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) return alert("Please select a CSV file first");

    const formData = new FormData();
    formData.append('file', file);

    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/predict', formData);
      setData(res.data);
    } catch (err) {
      console.error(err);
      alert("Upload failed or invalid file format.");
    } finally {
      setLoading(false);
    }
  };

  const handleDownload = () => {
    const csvContent = [
      ['ABSTRACT', 'Predicted_Tags'],
      ...data.map(row => [
        row.ABSTRACT.replace(/\n/g, ' ').replace(/"/g, '""'),
        row.Predicted_Tags.join(', ')
      ])
    ]
    .map(e => e.map(v => `"${v}"`).join(","))
    .join("\n");

    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.setAttribute("href", url);
    link.setAttribute("download", "predictions.csv");
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div style={{ minHeight: '100vh', backgroundColor: '#f7f9fc', padding: '2rem' }}>
      <div style={{ maxWidth: '1000px', margin: '0 auto', backgroundColor: '#fff', padding: '2rem', borderRadius: '12px', boxShadow: '0 0 10px rgba(0,0,0,0.1)' }}>
        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '2rem' }}>
          <img src="/logo192.png" alt="Logo" style={{ width: '50px', height: '50px', marginRight: '1rem' }} />
          <h1 style={{ fontSize: '2rem', fontWeight: 'bold', color: '#333' }}>Article Tag Predictor</h1>
        </div>

        <input type="file" accept=".csv" onChange={handleFileChange} style={{ marginBottom: '1rem' }} />
        <div style={{ display: 'flex', gap: '1rem', marginBottom: '1.5rem' }}>
          <button onClick={handleUpload} style={{ backgroundColor: '#007bff', color: 'white', padding: '0.5rem 1rem', border: 'none', borderRadius: '4px' }}>Predict Tags</button>
          {data.length > 0 && (
            <button onClick={handleDownload} style={{ backgroundColor: '#28a745', color: 'white', padding: '0.5rem 1rem', border: 'none', borderRadius: '4px' }}>Download CSV</button>
          )}
        </div>

        {loading && <p>Processing...</p>}

        {data.length > 0 && (
          <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '2rem' }}>
            <thead>
              <tr>
                <th style={{ border: '1px solid #ccc', padding: '0.5rem', backgroundColor: '#f0f0f0' }}>Abstract</th>
                <th style={{ border: '1px solid #ccc', padding: '0.5rem', backgroundColor: '#f0f0f0' }}>Predicted Tags</th>
              </tr>
            </thead>
            <tbody>
              {data.map((row, idx) => (
                <tr key={idx}>
                  <td style={{ border: '1px solid #ccc', padding: '0.5rem', verticalAlign: 'top' }}>{row.ABSTRACT}</td>
                  <td style={{ border: '1px solid #ccc', padding: '0.5rem', color: '#555' }}>{row.Predicted_Tags.join(", ")}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}
