import React, { useState, useRef } from 'react';
import axios from 'axios';

export default function App() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [dragActive, setDragActive] = useState(false);
  const inputRef = useRef(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      setFile(e.dataTransfer.files[0]);
    }
  };

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleUpload = async () => {
    if (!file) return alert('Please select a CSV file first');
    const formData = new FormData();
    formData.append('file', file);
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/predict', formData);
      setData(res.data);
    } catch (err) {
      console.error(err);
      alert('Upload failed or invalid file format.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-white flex items-center justify-center px-4 py-10">
      <div className="bg-white shadow-2xl rounded-3xl p-10 w-full max-w-4xl border border-gray-100">
        <h1 className="text-4xl font-extrabold text-center text-blue-700 mb-10 tracking-tight">
          Article Tag Predictor
        </h1>

        {/* Drag & Drop Upload Area */}
        <div
          className={`transition-all duration-200 border-2 border-dashed rounded-xl p-8 mb-8 flex flex-col items-center justify-center cursor-pointer
            ${dragActive ? 'border-blue-400 bg-blue-50' : 'border-gray-300 bg-gray-50 hover:border-blue-300'}`}
          onClick={() => inputRef.current.click()}
          onDragEnter={handleDrag}
          onDragLeave={handleDrag}
          onDragOver={handleDrag}
          onDrop={handleDrop}
        >
          <input
            ref={inputRef}
            type="file"
            accept=".csv"
            onChange={handleFileChange}
            className="hidden"
          />
          <svg className="w-12 h-12 text-blue-300 mb-3" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" d="M12 16v-8m0 0-4 4m4-4 4 4M4 16h16" />
          </svg>
          <p className="text-lg text-gray-600 font-medium">
            Drag & drop your <span className="font-semibold text-blue-600">CSV</span> file here, or <span className="underline text-blue-700">browse</span>
          </p>
          {file && (
            <div className="mt-4 flex flex-col items-center text-sm text-gray-700">
              <span className="font-semibold">{file.name}</span>
              <span>{file.type} &middot; {(file.size / 1024).toFixed(1)} KB</span>
            </div>
          )}
        </div>

        <div className="flex justify-center mb-6">
          <button
            onClick={handleUpload}
            disabled={loading}
            className="bg-blue-600 hover:bg-blue-700 text-white px-8 py-3 rounded-xl font-semibold shadow-md transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loading ? (
              <span className="flex items-center gap-2">
                <svg className="animate-spin h-5 w-5 text-white" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8z"></path>
                </svg>
                Processing...
              </span>
            ) : (
              'Predict Tags'
            )}
          </button>
        </div>

        {data.length > 0 && (
          <div className="overflow-x-auto mt-8 rounded-xl shadow-sm">
            <table className="min-w-full border-collapse bg-white rounded-xl">
              <thead className="bg-blue-50">
                <tr>
                  <th className="p-5 text-left text-base font-semibold text-blue-800 rounded-tl-xl">Abstract</th>
                  <th className="p-5 text-left text-base font-semibold text-blue-800 rounded-tr-xl">Predicted Tags</th>
                </tr>
              </thead>
              <tbody>
                {data.map((row, idx) => (
                  <tr key={idx} className={idx % 2 === 0 ? "bg-white" : "bg-blue-50/60"}>
                    <td className="p-5 text-gray-900 text-sm align-top w-2/3 text-justify">{row.ABSTRACT}</td>
                    <td className="p-5 text-blue-700 text-sm align-top w-1/3">
                      {row.Predicted_Tags.map((tag, i) => (
                        <span
                          key={i}
                          className="inline-block bg-blue-100 text-blue-700 px-3 py-1 rounded-full mr-2 mb-1 text-xs font-medium"
                        >
                          {tag}
                        </span>
                      ))}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}
